{# System Management for Catalyst Only #}
{% include "DNAC-Templates-Samples/Sensitive-Info" %}
{% include "DNAC-Templates-Samples/VLAN-Configuration" %}

no service pad
service timestamps debug datetime msec localtime
service timestamps log datetime msec localtime
no platform punt-keepalive disable-kernel-core
!
spanning-tree mode rapid-pvst
spanning-tree portfast default
spanning-tree portfast bpduguard default
!
{%- include "DNAC-Templates-Samples/ACL-Configuration" -%}
!
snmp-server location {{ snmpLocation }} 
{% for snmpCommunityString in snmpCommunityStrings %}
  snmp-server community {{ snmpCommunityString['string'] }} RW 10
{% endfor %}
!
{% for user in users %}
  username {{ user.name }} priv {{ user.priv }} secret 9 {{ user.key }}
{% endfor %}
!

