templates.templateContent:
{# General Interface Macro Section#}
!
{# Interface Access Macro #}
{% macro access_interface(vlan_number) %}
  switchport access vlan {{ vlan_number }}
  switchport mode access
  snmp trap mac-notification change added
  snmp trap mac-notification change removed
  spanning-tree portfast
{% endmacro %}
!
{# Interface Access Point Macro for AP's #}
{% macro accesspoint(vlan_number) %} 
   description Access Point Interface
   switchport access vlan {{ vlan_number }}
   switchport mode access
   spanning-tree portfast
{% endmacro %}
!
{# Interface FLex-Access Point Macro for Flex AP's #}
{% macro flexaccesspoint(vlan_number, data_number, voice_number, guest_number) %} 
   description Flex Access Point Interface
   switchport mode trunk
   switchport trunk native vlan {{ vlan_number }}
   switchport trunk allowed vlan {{ vlan_number }},{{ data_number }},{{ voice_number}},{{ guest_number }}
   spanning-tree portfast trunk
{% endmacro %}
!
{# Interface Workstations with Voice Macro #}
{% macro workstation_interface(vlan_number, voice_number) %}
  description Workstation Interface
  switchport access vlan {{ vlan_number }}
  switchport voice vlan {{ voice_number }}
  switchport mode access
  snmp trap mac-notification change added
  snmp trap mac-notification change removed
  spanning-tree portfast
{% endmacro %}

{# --------------------------------------------------------------- #}

{# AutoConf Macro Section #}
{# AutoConf Base Configuration Macro #}
{% macro baseconf_interface() %}
     switchport mode access
     snmp trap mac-notification change added
     snmp trap mac-notification change removed
     source template BASE
{% endmacro %}
!
